children.text:
@charset "utf-8";

* {
	padding: 0;
	margin: 0;
	outline: 0;
}
body{
	font-size:14px;
	font-family: arial;
}
*::-webkit-scrollbar {
	width: 5px;
}

*::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
	-webkit-border-radius: 10px;
	border-radius: 10px;
}

*::-webkit-scrollbar-thumb {
	-webkit-border-radius: 10px;
	border-radius: 10px;
	background: rgba(0, 0, 0, 0.8);
	-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.5);
}

*::-webkit-scrollbar-thumb:window-inactive {
	background: rgba(0, 0, 0, 0.4);
}

header {
	height: 60px;
	line-height: 60px;
	background: #2D303B;
	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(rgba(81, 86, 100, .95)
		), color-stop(0.88, rgba(57, 61, 71, .95) ), to(rgba(51, 54, 63, .95)
		) );
	-webkit-box-shadow: 0 1px 3px rgba(0, 0, 0, .6), inset 0 1px 0
		rgba(255, 255, 255, .1), inset 0 -1px 0 rgba(0, 0, 0, .2);
	opacity: .90;
	-webkit-transform: translate3d(0, 0, 0);
	transform: translate3d(0, 0, 0);
	position: relative;
	z-index: 10;
	-webkit-box-sizing: border-box;
	display: block;
	color: #ccc;
}

html,body {
	height: 100%;
	width: 100%;
	overflow: hidden;
}

header h1 {
	padding-left: 10px;
}

a {
	text-decoration: none;
}

a:hover {
	text-decoration: underline;
}

.wrapper {
	position: absolute;
	top: 60px;
	left: 0;
	right: 0;
	bottom: 0;
}

.cate {
	position: absolute;
	top: 0px;
	left: 0;
	width: 190px;
	bottom: 0;
	padding: 5px;
	background: #2D303B;
	-webkit-box-sizing: border-box;
	border-right: 1px solid #161820;
}

.cate h2 {
	color: #AEAFBB;
	text-shadow: 0 1px 0 black;
	height: 30px;
	font-size: 15px;
	line-height: 30px;
	cursor: pointer;
}

.content {
	position: absolute;
	top: 0px;
	left: 190px;
	right: 0;
	bottom: 0;
	padding: 10px;
	line-height: 20px;
	overflow-y: auto;
	overflow-x: hidden;
	background: #363946;
}

.cate li {
	line-height: 24px;
	list-style-type: circle;
	margin-left: 15px;
}

.cate li a {
	color: #D8DAE3;
	height: 24px;
	line-height: 24px;
	cursor: pointer;
	display: block;
	padding-left: 5px;
	font-size: 16px;
}

.cate li a.active {
	background: -webkit-gradient(linear, 0 0, 0 100%, from(rgba(27, 30, 37, .5)
		), to(rgba(27, 30, 37, .35) ) );
	border-color: #1D1F26;
	-webkit-box-shadow: #3B3F48 0 1px 0, rgba(0, 0, 0, .25) 0 1px 2px inset;
	-moz-box-shadow: #3B3F48 0 1px 0, rgba(0, 0, 0, .25) 0 1px 2px inset;
	box-shadow: #3B3F48 0 1px 0, rgba(0, 0, 0, .25) 0 1px 2px inset;
	border-radius: 3px;
	color: #fff;
	font-weight: bold;
}

.content h2 {
	-webkit-box-shadow: inset 1px 0 0 rgba(255, 255, 255, .075);
	box-shadow: inset 1px 0 0 rgba(255, 255, 255, .075);
	background: -webkit-gradient(linear, 0 0, 0 100%, from(rgba(255, 255, 255, .1)
		), to(rgba(255, 255, 255, .02) ) );
	border-bottom: 1px solid #252830;
	height: 24px;
	line-height: 24px;
	padding-left: 10px;
	color: #888;
	font-size: 14px;
	margin: -10px -10px 0 -10px;
}

.add {
	position: fixed;
	bottom: 0;
	background: #fff;
	right: 10px;
	left: 200px;
	-webkit-border-top-left-radius: 5px;
	-webkit-border-top-right-radius: 5px;
	padding: 0 5px 5px 5px;
	opacity: 0.85;
}

textarea {
	width: 98%;
	display: block;
	height: 120px;
	border: 1px solid #ccc;
	-webkit-border-radius: 5px;
	padding: 5px;
	font-size: 13px;
	line-height: 16px;
}

input[type=text] {
	height: 18px;
	border: 1px solid #ccc;
	-webkit-border-radius: 3px;
	width: 60px;
}

button {
	padding: 3px 5px;
}

.result {
	position: absolute;
	top: 10px;
	left: 10px;
	right: 5px;
	bottom: 0px;
}

.list {
	position: absolute;
	top: 15px;
	left: 0px;
	right: 0px;
	bottom: 115px;
	overflow-x: hidden;
	padding-right: 10px;
	overflow-y: auto;
}

.result li {
	line-height: 22px;
	margin: 5px 0;
	width: 100%;
	padding-left: 5px;
	-webkit-border-radius: 5px;
	opacity: 0.7;
	font-size: 14px;
	display: block;
	position: relative;
	cursor: pointer;
	padding-bottom: 1px;
}

.result li span {
	display: block;
	height: 22px;
	overflow: hidden;
}

.result li .info {
	display: none;
	background: #ccc;
	margin: 5px;
	-webkit-border-radius: 5px;
	padding: 3px;
	line-height: 18px;
	opacity: 0.75;
}

.result li b.del {
	position: absolute;
	top: 0;
	right: 10px;
	cursor: pointer;
	font-size: 16px;
	display: none;
	opacity: 0.5;
}

.result li b.refresh {
	position: absolute;
	top: 5px;
	right: 25px;
	cursor: pointer;
	background: url("refresh.png") no-repeat;
	height: 12px;
	width: 12px;
	display: none;
	opacity: 0.5;
}

.result li:hover b {
	display: block;
}

.result li.correct {
	background: green
}

.result li.failure {
	background: red;
}

.add h2 {
	margin: 0;
	padding-left: 0;
	border-bottom: 0;
}

#testResultCon {
	-webkit-transition: all 0.3s ease-out;
}
